Colour.a\n        WeakAuras.regions[aura_env.id].region.bar.bg:SetVertexColor(aura_env.RechargeColour.br, aura_env.RechargeColour.bg, aura_env.RechargeColour.bb, aura_env.RechargeColour.ba);\n        return red, green, blue, alpha\n    elseif aura_env.RuneColourChange == true then\n        \n        if specID == 1 then\n            \n            red, green, blue, alpha = aura_env.BloodColour.r, aura_env.BloodColour.g, aura_env.BloodColour.b, aura_env.BloodColour.a\n            WeakAuras.regions[aura_env.id].region.bar.bg:SetVertexColor(aura_env.BloodColour.br, aura_env.BloodColour.bg, aura_env.BloodColour.bb, aura_env.BloodColour.ba);\n            return red, green, blue, alpha\n        elseif specID == 2 then\n            \n            red, green, blue, alpha = aura_env.FrostColour.r, aura_env.FrostColour.g, aura_env.FrostColour.b, aura_env.FrostColour.a\n            WeakAuras.regions[aura_env.id].region.bar.bg:SetVertexColor(aura_env.FrostColour.br, aura_env.FrostColour.bg, aura_env.FrostColour.bb, aura_env.FrostColour.ba);\n            return red, green, blue, alpha\n        elseif specID == 3 then\n            \n            red, green, blue, alpha = aura_env.UnholyColour.r, aura_env.UnholyColour.g, aura_env.UnholyColour.b, aura_env.UnholyColour.a\n            WeakAuras.regions[aura_env.id].region.bar.bg:SetVertexColor(aura_env.UnholyColour.br, aura_env.UnholyColour.bg, aura_env.UnholyColour.bb, aura_env.UnholyColour.ba);\n            return red, green, blue, alpha\n        end\n        \n    else \n        red, green, blue = r1, g1, b1\n        return red, green, blue, 1\n    end\nend",
